// *가 붙은 genrator 함수는 yield; 까지 실행하고, 그 다음에 .next() 해주면 그 다음 yield까지를 실행함.
// 중단점이 있는 함수! 원래 js 는 중단점이 없는데, gen 함수는 있음
// 이 성질을 활용한 것이 saga임.
// eventListener처럼 활용 가능

import { all, fork, call, take, put } from 'redux-saga/effects';
import axios from 'axios';

function logInAPI() {
    return axios.post('/api/login');
}

function* logIn() {
    try{
        const result = yield call(logInAPI);
        yield put({
            type: 'LOG_IN_SUCCESS',
            data: result.data,
        });
    } catch (err) {
        yield put({ // put은 dispatch와 거의 같음.
            type: 'LOG_IN_FAILURE',
            data: err.response.data,
        });
    }    
}

function logOutAPI() {
    return axios.post('/api/logout');
}

function* logOut() {
    try{
        const result = yield call(logOutAPI);
        yield put({
            type: 'LOG_OUT_SUCCESS',
            data: result.data,
        });
    } catch (err) {
        yield put({ // put은 dispatch와 거의 같음.
            type: 'LOG_OUT_FAILURE',
            data: err.response.data,
        });
    }    
}

function addPostAPI() {
    return axios.post('/api/post');
}

function* addPost() {
    try{
        const result = yield call(addPostAPI);
        yield put({
            type: 'ADD_POST_SUCCESS',
            data: result.data,
        });
    } catch (err) {
        yield put({ // put은 dispatch와 거의 같음.
            type: 'ADD_POST_FAILURE',
            data: err.response.data,
        });
    }    
}

//call은 동기 함수라서 결과 올떄까지 기다림 <-> fork는 비동기 함수 요청이라서, 결과 기다리지 않고 바로 다음줄 실행.
// yield는 await이랑 비슷한 역할이라고 생각해도 됨.

function* watchLogIn() {
    yield take('LOG_IN_REQUEST', logIn); //LOG_IN이 들어오면 logIn을 실행하는 일종의 이벤트리스너?
}

function* watchLogOut() {
    yield take('LOG_OUT_REQUEST', logOut);
}

function* watchAddPost() {
    yield take('ADD_POST_REQUEST', addPost);
}

export default function* rootSaga() {
    yield all([
        fork(watchLogIn), //fork는 함수를 실행하는 것. 
        fork(watchLogOut), //근데 call이랑은 다름. then, how different?
        fork(watchAddPost),
    ]);
}
