import React from 'react';
import { useSelector } from 'react-redux';
import { Card, Popover, Button, Avatar } from 'antd';
import PropTypes from 'prop-types';
import { RetweetOutlined,HeartOutlined, MessageOutlined, EllipsisOutlined,  } from '@ant-design/icons';

const PostCard = ({ post }) => {
    const id = useSelector((state) => state.user.me?.id); //?는 있으면~~ 이라는 조건문.
    return (
        <div>
            <Card
                cover={post.Images[0] && <PostImages images={post.images} />} //post는 index.js 에서 부모로부터 받아오고 있음. 
                actions ={[
                    <RetweetOutlined key="retweet"/>,
                    <HeartOutlined key="heart"/>,
                    <MessageOutlined key="comment"/>,
                    <Popover key="more" content={(
                        <Button.Group>
                            {id && post.User.id === id 
                                ? (
                                    <>
                                        <Button>수정</Button>
                                        <Button type='danger'>삭제</Button>
                                    </>
                                ) 
                                : <Button>신고</Button>} 
                        </Button.Group>
                    )}>
                        <EllipsisOutlined />
                    </Popover>
                ]}
            >
                <Card.Meta>
                    avatar={<Avatar>{post.User.nickname[0]}</Avatar>}
                    title={post.User.nickname}
                    description={post.content}
                </Card.Meta>
            </Card>
            {/* <CommentForm />
            <Comments /> */}
        </div>
    );
};

PostCard.propTypes = {
    post: PropTypes.shape({
        id: PropTypes.number,
        User: PropTypes.object,
        content: PropTypes.string,
        createdAt: PropTypes.object,
        Comments: PropTypes.arrayOf(PropTypes.object), //객체들의 배열이라는 뜻
        Images: PropTypes.arrayOf(PropTypes.object),
    }).isRequired,
};

export default PostCard;
